import { API } from "../../../backend";

export const newArticle = (userId, token, post) => {
  return fetch(`${API}/api/new/post/${userId}`, {
    method: "POST",
    header: {
      Accept: "application/json",
      "content-type": "application/json",
      Authorization: `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZmY2YTgxOTY0MTBmOTFiOTRjY2E2YzAiLCJpYXQiOjE2MTAwMDg3Mzh9.CgvZ4tb3bWXUZ_482fOoGNIj9XwvYpVcz9IR_5-rjWQ","user":{"_id":"5ff6a8196410f91b94cca6c0`,
    },
    body: post,
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => console.log(err));
};
