import React, { useState } from "react";
// import { useHistory } from "react-router-dom";
import { signup } from "../helper/apicalls";
import Style from "./signup.module.css";

const Signup = () => {
  const [value, setValue] = useState({
    username: "",
    email: "",
    password: "",
    error: "",
    success: false,
  });

  const { username, email, password, error, success } = value;

  const handleChange = (username) => (e) => {
    setValue({ ...value, error: false, [username]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    setValue({ ...value, error: false });
    signup({ username, email, password })
      .then((data) => {
        if (data.error) {
          setValue({ ...value, error: data.error, success: false });
        } else {
          setValue({
            ...value,
            username: "",
            email: "",
            password: "",
          });
        }
      })
      .catch((err) => console.log("error in signup"));
  };

  const signupForm = () => {
    return (
      <div className={Style.login}>
        <form>
          <h2>Signup</h2>
          <input
            placeholder="username"
            type="text"
            onChange={handleChange("username")}
            required
            name="username"
          />
          <input
            placeholder="Email"
            type="email"
            onChange={handleChange("email")}
            required
            name="email"
          />
          <input
            placeholder="Password"
            type="password"
            onChange={handleChange("password")}
            required
            name="password"
          />
          <button onClick={onSubmit} type="submit">
            submit
          </button>
        </form>
      </div>
    );
  };

  return { signupForm };
};

export default Signup;
