import { API } from "../../../backend";

export const newArticle = (userId, token, post) => {
  return fetch(`${API}/api/new/post/${userId}`, {
    method: "POST",
    header: {
      Accept: "application/json",
      "Content-type": "application/json",
      Authorization: `Bearer ${token}`,
    },
    body: post,
  });
};
