  //     key={post._id}
  //     className={Style.button}
  //     onClick={() => history.push(`/article/${post.slug}`)}
  //   >
  //     <Blogheader
  //       title={post.title}
  //       description={post.description}
  //       createdAt={post.createdAt}
  //     />
  //   </button>
  // ));

  // };

  return (
    <div className={Style.home}>
      {posts.map((post) => {
        return (
          <>
            <button
              key={post._id}
              className={Style.button}
              onClick={() => history.push(`/article/${post.slug}`)}
            >
              <Blogheader
                title={post.title}
                description={post.description}
                createdAt={post.createdAt}
              />
            </button>
          </>
        );
      })}
    </div>
  );
};

export default Home;
