import React, { useState } from "react";
import Style from "./NewArticle.module.css";
import { isAuthenticated } from "../helper/apicalls";
import { newArticle } from "./helper/apicall";

const NewArticle = () => {
  const [post, setPost] = useState({
    title: "",
    description: "",
    markdown: "",
  });

  // const { title, description, markdown } = post;
  const [error, setError] = useState(false);
  const [success, setSuccess] = useState(false);
  const { user, token } = isAuthenticated();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setError("");
    setPost({
      ...post,
      [name]: value,
    });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    setError("");
    setSuccess(false);
    console.log(token);

    newArticle(user._id, token, { post })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((data) => {
        console.log(data);
        if (data.error) {
          setError(true);
        } else {
          setError("");
        }
      })
      .catch((err) => console.log(err));
  };

  return (
    <form className={Style.form}>
      {/* <input type="text" required placeholder="Url image layout" name="image" /> */}
      <input
        type="text"
        required
        placeholder="Title"
        value={post.title}
        name="title"
        onChange={handleChange}
      />
      <input
        type="text"
        required
        placeholder="Description"
        value={post.description}
        name="description"
        onChange={handleChange}
      />
      <textarea
        required
        placeholder="Markdown here"
        value={post.markdown}
        name="markdown"
        onChange={handleChange}
      />
      <div>
        <button>Back</button>
        <button onClick={onSubmit} type="submit">
          create
        </button>
      </div>
    </form>
  );
};

export default NewArticle;
