import React, { useEffect, useState } from "react";
import Style from "./Home.module.css";
import Blogheader from "../../components/blogheader/index";
import { useHistory } from "react-router-dom";
import { API } from "../../backend";

const Home = () => {
  let history = useHistory();
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    fetch(`${API}/api/post`)
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setPosts(data);
      })
      .catch((err) => {
        return console.log(err);
      });
  };

  posts.map((post) => {
    console.log("Slug", post.slug);
    console.log("title", post.title);
    console.log("description", post.description);
    console.log("markdown", post.markdown);
  });
  const renderPost = () => {
    // if (posts.length === 0) {
    //   return <i className="gg-spinner-two"></i>;
    // } else {
    return posts.map((post) => (
      <button
        key={post._id}
        className={Style.button}
        onClick={() => history.push(`/article/${post.slug}`)}
      >
        <Blogheader
          title={post.title}
          description={post.description}
          createdAt={post.createdAt}
        />
      </button>
    ));
  };
  // };

  return <div className={Style.home}>{renderPost()}</div>;
};

export default Home;
